import { StyleSheet, Text, View, Image,TouchableOpacity } from 'react-native';
import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

import DonorScreen from './DonorScreen';
import ClientScreen from './ClientScreen';

const Stack = createNativeStackNavigator();


function WelcomeScreen({navigation}) {
  <NavigationContainer>
      <Stack.Navigator>
      <Stack.Screen name="DonorScreen" component={DonorScreen} />
      <Stack.screen name="ClientScreen" component={ClientScreen} />

      </Stack.Navigator>
    </NavigationContainer>
  return (
    <View style={styles.rootContainer}>
      <Text style={styles.title}>ברוכים הבאים!</Text>

      <View style={styles.imagesContainer}>
        <TouchableOpacity onPress={ () => navigation.navigate('DonorScreen')}>
          <View>
            <Image  style={styles.image}  source={require("../assets/Images/logoDonate.png")} />
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={ () => navigation.navigate('ClientScreen')}>
          <View>
            <Image style={styles.image}  source={require("../assets/Images/logoReciver.png")} />
          </View>
        </TouchableOpacity>

      </View>
    </View>
  );
}




export default WelcomeScreen;

const styles = StyleSheet.create({
  rootContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 32,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  imagesContainer:{
    flexDirection: 'row',
    justifyContent: 'space-between',
    margin:50,
    padding:50,

  },
  image:{
    height:150,
    width:150,
    margin:10
  }


});
